import React, { Component } from 'react';
import "./App.css"

import GameTable from './GameTable/GameTable';
import Alert from './Alert/Alert';

class App extends Component {
    state = {
        alert: false
    }

    render() {
        let {alert} = this.state
        if (alert){
            return (
                <div id='app'>
                    <GameTable key='main'/>
                    <Alert text="Youcan not play this card"/>
                </div>
            );
        }
        else{
            return (
                <div id='app'>
                    <GameTable key='main'/>
                </div>
            );
        }

    }
}

export default App;
