import React, { Component } from 'react';
import "./App.css"

import GameTable from './GameTable/GameTable';
import Alert from './Alert/Alert';

class App extends Component {
    state = {
        alert: false
    }

    render() {
        let {alert} = this.state
        return (
            <div id='app'>
                <GameTable key='main'/>
                {alert ? <Alert/>:""}
            </div>
        );

    }
}

export default App;
