import React, { Component } from 'react';

import Card from '../Card/Card';

class GameTable extends Component {
    _colors = ["yellow", "green", "blue", "red"]

    state = {
        player: [],
        bots: [],
        colode: [],
        playedCards: [],
        gameDirection: true
    }

    _createCard(color, name, size, posX, posY){
        return {
            color, 
            name, 
            size,
            posX,
            posY
        }
    }

    _generateColode(){
        let colode = []
        let count = 0
        for(let color in this._colors){
            for(let i = 0; i<10; i++){
                colode.push(this._createCard(color, i, 150, 45, 45+count))
                count -= 0.1
            }
        }
        return colode
    }

    componentDidMount(){

        this.setState({colode: this._generateColode()})
    }

    render() {
        let {colode} = this.state
        console.log(colode)
        return (
            <div id='game'>
                {
                    colode.map((element, id)=>{return <Card key={id} {...element}/>})
                }
            </div>
        );
    }
}

export default GameTable;
