import React, { Component } from 'react';

import errorImg from "./card-asset/card_back_alt_large.png"
import back from "./card-asset/card_back_large.png"
import "./Card.css"

class Card extends Component {

    state = {
        add: 0,
        onTop: 0
    }

    _hover = (evt) => {
        if(evt.target.alt != "back"){
            this.setState({
                addY: -3,
                onTop: 1000
            })
        }
    }

    render() {
        let image = errorImg
        let {props: {color, cardName, face, size, posX, posY, rot, z, id}, state: {addY, onTop}} = this
        let alt = `${color}_${cardName}`
        try {
            image = require(`./card-asset/${color}_${cardName}_large.png`)
        } catch (error) {
            image = errorImg
            alt = `${color}_${cardName}, not found :(`
            console.error(`${color}_${cardName}, not found :(`)
        }

        let style = {
            width: size,
            top: posY + (addY ?? 0) + "vh",
            left: posX + "vw",
            transform: `rotate(${rot}deg)`,
            zIndex: Math.max(z, onTop)
        }

        if(face){
            return (
                <img onMouseOver={this._hover} style={style} id={id} className='cards' src={image} alt={alt} />
            );
        }
        else{
            return (
                <img onMouseOver={this._hover} style={style} id={id} className='cards' src={back} alt={"back"} />
            );
        }


    }
}

export default Card;
