import React, { Component } from 'react';

import Card from '../Card/Card';

class GamePreview extends Component {

    state = {}

    _createCard = (color, cardName, size, posX, posY, face, addIndx = '', rot = 0, z = 1)=>{
        this.setState({
            [color[0] + (cardName[0] ?? cardName) + addIndx]: {
                color,
                cardName, 
                size, 
                posX,
                posY, 
                face,
                id: color[0] + (cardName[0] ?? cardName) + addIndx, 
                rot,
                z
            }
        })

    }

    componentDidMount(){
        console.log("a");
        let i = 0
        setInterval(()=>{
            i++
            this._createCard(["red", "blue", "yellow", "green"][Math.floor(Math.random()*3)], 
            [1, 2, 3, 4, 5, 6, 7, 8, 9, "picker", "stoper", 'reverse'][Math.floor(Math.random()*11)],
            150,
            Math.floor(Math.random()*100),
            Math.floor(Math.random()*100),
            Math.floor(Math.random()*2),
            i,
            Math.floor(Math.random()*360),
            i)
            console.log(i)
        }, 400)
    }

    render() {
        let cards = this.state
        return (
            <div id='preview'>
                <button>Start Game</button>
                {
                    Object.values(cards).map((element)=>{return <Card key = {element.id} {...element}/>})
                }
            </div>
        );
    }
}

export default GamePreview;
