import React, { Component } from 'react';

class Card extends Component {
    render() {
        let {color, cardName} = this.props
        let image = require(`./card-asset/${color}_${cardName}_large.png`)
        return (
            <img src={image} alt="" />
        );
    }
}

export default Card;
